import React, { Component } from "react";
import Selector from "../../components/Selector/Selector";
import Tabletop from "tabletop";
import GoogleMapReact from "google-map-react";

class GymFinder extends Component {
  state = {
    gymOptions: []
  };

  static defaultProps = {
    center: {
      lat: 59.95,
      lng: 30.33
    },
    zoom: 11
  };
  componentDidMount() {
    Tabletop.init({
      key:
        "https://docs.google.com/spreadsheets/d/1DQUjeYhEBRemcZObEKBj-QvxISX-sVUpbY8yPadg7BY/edit?usp=sharing",
      callback: this.showInfo,
      simpleSheet: true
    });
  }

  showInfo = (data, tabletop) => {
    let gyms = tabletop.sheets("Coords").toArray();
    let gymOptions = gyms.map(element => {
      //   console.log("the element is ", element[0]);

      if (element[1] === "") {
        return {
          label: element[0],
          isDisabled: true
          //   style: "bold"
        };
      }
      return {
        value: element[0],
        label: element[0],
        coordinates: element[1]
      };
    });
    this.setState({ gymOptions: gymOptions });
  };
  render() {
    const AnyReactComponent = ({ text }) => <div>{text}</div>;

    return (
      <div>
        <Selector type="input" options={this.state.gymOptions} label="Gym" />

        <div style={{ width: "50%", height: "50%" }}>
          <GoogleMapReact
            bootstrapURLKeys={{
              key: "AIzaSyCJDjV4MIrjB9iBfGps4EsWnSMOmUcbkaQ"
            }}
            defaultCenter={this.props.center}
            defaultZoom={this.props.zoom}
          >
            <AnyReactComponent
              lat={59.955413}
              lng={30.337844}
              text="My Marker"
            />
          </GoogleMapReact>
        </div>
      </div>
    );
  }
}

export default GymFinder;
