import React, { Component } from "react";
import logo from "./logo.svg";

import "./App.css";
import Selector from "./components/Selector/Selector";
import Button from "./components/Button/Button";

class App extends Component {
  state = {
    pokemonOptions: [],
    gymOptions: [],
    time: "",
    selectedRaidBoss: null,
    selectedGym: null,

    copyValue: []
  };

  handleChange = (key, selectedOption) => {
    this.setState({ [key]: selectedOption });
  };

  handleRaidBossChange = selectedRaidBoss => {
    // console.log("the event is ", event.value);
    this.setState({
      selectedRaidBoss: selectedRaidBoss.value,
      copyValue: selectedRaidBoss
    });
  };
  handleGymChange = selectedGym => {
    // console.log("the event is ", event.value);
    this.setState({
      selectedGym: selectedGym,
      copyValue: selectedGym
    });
  };
  render() {
    const options = [
      { value: "-", label: "-" },
      { value: "chocolate", label: "Chocolate" },
      { value: "strawberry", label: "Strawberry" },
      { value: "vanilla", label: "Vanilla" }
    ];
    console.log("this.state.selected", this.state);
    return (
      //either keep the container or use media queries to reduce the size depended on screensize
      <div className="container">
        {/* //toolbar 
      //search bar
      //search bar
      // <Selector />
      //search bar */}
        {/* //copy button  */}

        <Selector
          label="Raid Boss"
          changed={(val) =>this.handleRaidBossChange("selectedRaidBoss")}
          selected={this.state.selectedRaidBoss}
          options={options}
        />
        <Selector
          label="Gym"
          changed={this.handleGymChange}
          selected={this.state.selectedGym}
          options={options}
        />

        <label style={{ color: "#6c7d8e" }}>Start Time</label>
        <div className="form-group">
          <input
            type="time"
            class="ratime form-control"
            placeholder="Time"
            required=""
            data-fillr-id="1465670472"
            data-fillr="bound"
            autoComplete="off"
          />
        </div>
        {/* <Selector
          label="Start Time"
          changed={this.handleChanged}
          selected={this.state.selectedOption}
          type="time"
        /> */}
        <Button />
      </div>
    );
  }
}

export default App;
