import React, { Component } from "react";
import logo from "./logo.svg";

import "./App.css";
import Selector from "./components/Selector/Selector";
import Button from "./components/Button/Button";

class App extends Component {
  state = {
    pokemonOptions: [],
    gymOptions: [],
    time: "",
    selectedOption: null
  };

  handleChanged = event => {
    console.log(event.target);
    this.setState({ selectedOption: event.target });
  };
  render() {
    console.log("this.state.selected", this.state.selectedOption);
    return (
      //either keep the container or use media queries to reduce the size depended on screensize
      <div className="container">
        {/* //toolbar 
      //search bar
      //search bar
      // <Selector />
      //search bar */}
        {/* //copy button  */}

        <Selector
          label="Raid Boss"
          changed={this.handleChanged}
          selected={this.state.selectedOption}
        />
        <Selector label="Gym" />
        <Selector label="Start Time" />
        <Button />
      </div>
    );
  }
}

export default App;
