import React, { Component } from "react";
import Tabletop from "tabletop";
import classes from "./Gym.module.css";

class Gym extends Component {
  state = {
    // id: this.props.match.params.gymId,
    data: []
  };

  componentDidMount() {
    console.log("the id is", this.props.match.params.gymId);

    Tabletop.init({
      key:
        "https://docs.google.com/spreadsheets/d/15mSqg-uGZTmQBEu_hqvNzaCiSPGi8JapuV46WmsDRvM/edit?usp=sharing",
      callback: this.showInfo,
      simpleSheet: true
    });
  }
  showInfo = (data, tabletop) => {
    console.log("the id is", this.props.match.params.gymId);
    const array = tabletop.sheets(this.props.match.params.gymId).toArray();
    this.setState({ data: array });
  };
  makeList = data => {
    console.log("the datae is ", data);
    let date = "";
    let time = "";
    let list = "";
    let instinctList = "";
    let valorList = "";
    let mysticList = "";
    if (data && data[0]) {
      date = data[0][4];
      time = data[0][5];
      console.log("inside the if");
      console.log(data[0][4]);

      instinctList = data.filter(element => element[2] === "Instinct");

      valorList = data.filter(element => element[2] === "Valor");

      mysticList = data.filter(element => element[2] === "Mystic");
    }
    console.log("the list is ", list);

    return (
      <div className={classes.Gym}>
        <span>{date}</span>
        <span>{time}</span>
        <div>{generateList(instinctList)}</div>
        <div>{generateList(mysticList)}</div>
        <div>{generateList(valorList)}</div>
      </div>
    );
  };
  generateList = list => {
    let finishedList = "";
    finishedList = list.map(element => {
      if (
        element[6].trim() ===
        "My friend will play for me (enter their name in the comment)"
      ) {
        return (
          <li>
            {element[0].trim() +
              ", " +
              element[1].trim() +
              ", " +
              element[3].trim() +
              ", " +
              "via proxy"}
          </li>
        );
      } else if (
        element[6].trim() ===
        "I need someone to play for me (add your telegram name in the comments so an admin can contact you)"
      ) {
        return (
          <li>
            {element[0].trim() +
              ", " +
              element[1].trim() +
              ", " +
              element[3].trim() +
              ", " +
              "needs proxy"}
          </li>
        );
      } else {
        return (
          <li>
            {element[0].trim() +
              ", " +
              element[1].trim() +
              ", " +
              element[3].trim()}
          </li>
        );
      }
    });
    return finishedList;
  };
  render() {
    // console.log("the id is", this.state.data[0]);
    // let date = this.state.data[0];
    let time = "";
    // this.state.data.map(element => {
    //   date = element[4];
    //   time = element[5];
    // });
    return (
      <div>
        <h1 className={classes.Gym}>{this.props.match.params.gymId}</h1>
        <div>{this.makeList(this.state.data)}</div>
      </div>
    );
  }
}

export default Gym;
