import React, { Component } from "react";
import Tabletop from "tabletop";
import classes from "./Gym.module.css";

class Gym extends Component {
  state = {
    // id: this.props.match.params.gymId,
    data: []
  };

  componentDidMount() {
    console.log("the id is", this.props.match.params.gymId);

    Tabletop.init({
      key:
        "https://docs.google.com/spreadsheets/d/15mSqg-uGZTmQBEu_hqvNzaCiSPGi8JapuV46WmsDRvM/edit?usp=sharing",
      callback: this.showInfo,
      simpleSheet: true
    });
  }
  showInfo = (data, tabletop) => {
    console.log("the id is", this.props.match.params.gymId);
    const array = tabletop.sheets(this.props.match.params.gymId).toArray();
    this.setState({ data: array });
  };
  makeList = data => {
    console.log("the datae is ", data);
    let date = "";
    let time = "";
    if (data && data[0]) {
      date = data[0][4];
      time = data[0][5];
      console.log("inside the if");
      console.log(data[0][4]);

      const list = data.map(element => {
        if (
          element[6].trim() ===
          "My friend will play for me (enter their name in the comment)"
        ) {
          return (
            <li>
              {element[0].trim() +
                ", " +
                element[1].trim() +
                ", " +
                element[3].trim() +
                ", " +
                "via proxy"}
            </li>
          );
        } else if (
            element[6].trim() ===
          "I need someone to play for me (add your telegram name in the comments so an admin can contact you)"
        ) {
          return (
            <li>
              {element[0].trim() +
                ", " +
                data[1].trim() +
                ", " +
                data[3].trim() +
                ", " +
                "needs proxy"}
            </li>
          );
        } else {
          return (
            <li>
              {data[0].trim() + ", " + data[1].trim() + ", " + data[3].trim()}
            </li>
          );
        }
      });
    }

    return (
      <div className={classes.Gym}>
        <span>{date}</span>
        <span>{time}</span>
        {/* <ul>{list} </ul> */}
      </div>
    );
  };
  render() {
    // console.log("the id is", this.state.data[0]);
    // let date = this.state.data[0];
    let time = "";
    // this.state.data.map(element => {
    //   date = element[4];
    //   time = element[5];
    // });
    return (
      <div>
        <h1 className={classes.Gym}>{this.props.match.params.gymId}</h1>
        <div>{this.makeList(this.state.data)}</div>
      </div>
    );
  }
}

export default Gym;
