    console.log(array[0][0]);
    this.setState({ data: array });
  };
  makeList = data => {
    console.log("the datae is ", data);
    let date = "";
    let time = "";
    let list = "";
    let instinctList = "";
    let valorList = "";
    let mysticList = "";
    if (data && data[0]) {
      date = data[0][4];
      time = data[0][5];
      console.log("inside the if");
      console.log(data[0][4]);

      instinctList = data.filter(element => element[2] === "Instinct");

      valorList = data.filter(element => element[2] === "Valor");

      mysticList = data.filter(element => element[2] === "Mystic");

      console.log("the mystic list is ", mysticList);

      return (
        <div className={classes.Gym}>
          <span className={classes.Gym_date_time}>{date}</span>
          <span className={classes.Gym_date_time}>{time}</span>
          <div className={classes.Gym_Container}>
            <ul className={classes.InstinctList}>
              {this.generateList(instinctList)}
            </ul>
          </div>
          <div className={classes.Gym_Container}>
            <ul className={classes.MysticList}>
              {this.generateList(mysticList)}
            </ul>
          </div>
          <div className={classes.Gym_Container}>
            <ul className={classes.ValorList}>
              {this.generateList(valorList)}
            </ul>
          </div>
        </div>
      );
    }
  };
  generateList = list => {
    console.log("the list is", list);
    let finishedList = "";
    finishedList = list.map(element => {
      if (
        element[6].trim() ===
        "My friend will play for me (enter their name in the comment)"
      ) {
        return (
          <li>
            {element[0].trim() +
              ", " +
              element[1].trim() +
              ", " +
              element[3].trim() +
              ", " +
              "via proxy"}
          </li>
        );
      } else if (
        element[6].trim() ===
        "I need someone to play for me (add your telegram name in the comments so an admin can contact you)"
      ) {
        return (
          <li>
            {element[0].trim() +
              ", " +
              element[1].trim() +
              ", " +
              element[3].trim() +
              ", " +
              "needs proxy"}
          </li>
        );
      } else {
        return (
          <li>
            {element[0].trim() +
              ", " +
              element[1].trim() +
              ", " +
              element[3].trim()}
          </li>
        );
      }
    });
    return finishedList;
  };
  render() {
    // console.log("the id is", this.state.data[0]);
    // let date = this.state.data[0];
    let time = "";
    // this.state.data.map(element => {
    //   date = element[4];
    //   time = element[5];
    // });
    return (
      <div>
        <h1 className={classes.Gym}>{this.props.match.params.gymId}</h1>
        <div className={classes.List}>{this.makeList(this.state.data)}</div>
      </div>
    );
  }
}

export default Gym;
